import React, { Component } from 'react';
import axios from 'axios';

const PORT = process.env.PORT || 4000; // "process.env.PORT" is Heroku's port if we're deploying there, then 4000 is a custom chosen port for dev testing

export default class CreateExpense extends Component {

    constructor(props) {
        super(props);

        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeAmount = this.onChangeAmount.bind(this);
		this.onChangeMonth = this.onChangeMonth.bind(this);
		this.onChangeYear = this.onChangeYear.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            description: '',
            amount: '',
            month: '',
            year: ''
        }
    }

    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        });
    }

    onChangeAmount(e) {
        this.setState({
            amount: e.target.value
        });
    }
	
	onChangeMonth(e) {
        this.setState({
            month: e.target.value
        });
    }
	
	onChangeYear(e) {
        this.setState({
            year: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();
        
        console.log(`Form submitted:`);
        console.log(`Description: ${this.state.description}`);
        console.log(`Amount: ${this.state.amount}`);
		console.log(`Month: ${this.state.month}`);
		console.log(`Year: ${this.state.year}`);
     
        const newExpense = {
            description: this.state.description,
            amount: this.state.amount,
            month: this.state.month,
            year: this.state.year
        };

        axios.post('/expenses/add', newExpense)
            .then(res => console.log(res.data));
 
		this.setState = {
            description: '',
            amount: '',
            month: '',
            year: ''
        }
    }

    render() {
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New Expense</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>Description: </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.description}
                                onChange={this.onChangeDescription}
                                />
                    </div>
                    <div className="form-group">
                        <label>Amount: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.amount}
                                onChange={this.onChangeAmount}
                                />
                    </div>
					<div className="form-group"> 
                        <label>Month: </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.month}
                                onChange={this.onChangeMonth}
                                />
                    </div>
					<div className="form-group"> 
                        <label>Year: </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.year}
                                onChange={this.onChangeYear}
                                />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Expense" className="btn btn-success" />
                    </div>
                </form>
				
				<form onSubmit={this.onSubmit}>

                    <div className="form-group">
                        <input type="submit" value="Delete Expense" className="btn btn-danger" />
                    </div>
                </form>
            </div>
        )
    }
}
