import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Expense = props => (
    <tr>
        <td>{props.item.description}</td>
        <td>{props.item.amount}</td>
        <td>{props.item.month}</td>
        <td>{props.item.day}</td>
        <td>{props.item.year}</td>
        <td>
            <Link to={"/edit/"+props.item._id}>Edit</Link>
        </td>
    </tr>
)

export default class TodosList extends Component {

    constructor(props) {
        super(props);
        this.state = {
			todos: [],
			total: 0,
			i: 0
		};
    }

    componentDidMount() {
        axios.get('/expenses/getAllExpenses')
            .then(response => {
                this.setState({ todos: onChangeSort(response.data) });
            })
            .catch(function (error){
                console.log(error);
            })
    }
	
	onChangeSort(sortList) {
		sortList.sort(function(a, b) {
			return a.description > b.description;
		});
		
		console.log(sort);
		return sortList;
    }

    todoList() {
        return this.state.todos.map(function(currentExpense, i){
            return <Expense item={currentExpense} key={i} />;
        })
    }
	
	totalAmount(listOfExpenses) {
		for(this.state.i = 0; this.state.i <this.state.todos.length; this.state.i++)
			this.state.sum += this.state.todos[this.state.i].amount;
		
	}

    render() {	
        return (
            <div>
                <h3><center>All Expenses</center></h3>
                <h5>Total: ${this.state.total} </h5>
                <table data-sort-name="description" 
				  data-sort-order="desc" 
				  className="table table-striped table-bordered" 
				  style={{ marginTop: 30 }} >
				  
                    <thead className="thead-dark">
                        <tr>
                            <th onClick={() => {this.onChangeSort('description')}}>Description</th>
                            <th onClick={() => {this.onChangeSort('amount')}}>Amount</th>
                            <th onClick={() => {this.onChangeSort('month')}}>Month</th>
                            <th onClick={() => {this.onChangeSort('day')}}>Day</th>
                            <th onClick={() => {this.onChangeSort('year')}}>Year</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
						<tr>
                <td>Tiger Nixon</td>
                <td>System Architect</td>
                <td>Edinburgh</td>
                <td>61</td>
                <td>2011/04/25</td>
                <td>$320,800</td>
            </tr>
            <tr>
                <td>Garrett Winters</td>
                <td>Accountant</td>
                <td>Tokyo</td>
                <td>63</td>
                <td>2011/07/25</td>
                <td>$170,750</td>
            </tr>
                        { this.todoList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
